import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { HttpHeaders } from '@angular/common/http';
import { WebSocketService } from '../../../Services/webSocket.service';
import { catchError, throwError } from 'rxjs';
import { ReactiveFormsModule,FormBuilder, FormGroup } from '@angular/forms';



@Component({
  selector: 'app-stocks',
  templateUrl: './stocks.component.html',
  styleUrls: ['./stocks.component.css']
})
export class StocksComponent implements OnInit {
  stocks: StocksDetails[];
  orders: OrdersDetails[];
  form: FormGroup;
  isModalOpen = false;
  isModalHistoryOpen = false;

  constructor(private fb: FormBuilder, private http: HttpClient, private webSocketService: WebSocketService) { // Inject WebSocketService
    this.stocks = [];
    this.orders = [];
    this.form = this.fb.group({
      stockSymbol: [''],
      orderType: [''],
      quantity: ['']
    });
  }
  openModal() {
    this.isModalOpen = true;
  }

  closeModal() {
    this.isModalOpen = false;
  }
  openHistoryModal() {
    const headers = new HttpHeaders().set('X-Api-Key', 'EDIG_Assessment');
    this.http.get<OrdersDetails[]>('http://localhost:7272/order', { headers }).subscribe(data => {
      this.orders = data;
    });

    this.isModalHistoryOpen = true;
  }

  closeHistoryModal() {
    this.isModalHistoryOpen = false;
  }

  ngOnInit() {
    this.getStocks();
  }

  getStocks() {
    const headers = new HttpHeaders().set('X-Api-Key', 'EDIG_Assessment');

    this.http.get<StocksDetails[]>('http://localhost:7272/stocks', { headers }).subscribe(data => {
      this.stocks = data;
    });

    this.webSocketService.getMessages().pipe(
      catchError(err => {
        console.error('Error in subscribe:', err);
        return throwError(err);
      })
    ).subscribe((PriceUpdate: StockUpdate) => { // Specify the type of update
      console.log("frontend updating");
      // Find the stock that was updated
      const stock = this.stocks.find(s => s.symbol === PriceUpdate.symbol);

      if (stock) {
        // Get the previous price from PriceHistories
        const previousPrice = stock.priceHistories[stock.priceHistories.length - 1].price;

        // Update the current price
        stock.currentPrice = PriceUpdate.price;

        // Add the new price to PriceHistories
        stock.priceHistories.push({ price: PriceUpdate.price, timeStamp: new Date().toISOString() });
      }
    });
  }
  placeOrder() {
    const headers = new HttpHeaders().set('X-Api-Key', 'EDIG_Assessment');
    const order = {
      Symbol: this.form.get('stockSymbol')?.value,
      Type: this.form.get('orderType')?.value,
      Quantity: this.form.get('quantity')?.value,
      TimeStamp: new Date().toISOString()
    };

    this.http.post('http://localhost:7272/order', order, { headers }).subscribe(response => {
      console.log(response);
    });
    this.closeModal();
  }

}

export interface StocksDetails {
  title: string,
  symbol: string,
  currentPrice: number,
  priceHistories: PriceHistory[]
}
export interface OrdersDetails {
  symbol: string,
  type: string,
  quantity: number,
  timeStamp: string
}


export interface PriceHistory {
  price: number,
  timeStamp: string
}
export interface StockUpdate {
  symbol: string;
  price: number;
}
<h3 class="text-md-center m-5">Sample Stocks</h3>
<div style="background-color:snow; border-radius: 10px; box-shadow: 5px 5px 15px rgba(0,0,0,0.3); padding: 20px;">
  <div style="flex-direction:row">
    <!-- Place Order Button -->
    <button type="button" class="btn btn-primary" (click)="openModal()">
      Place Order
    </button>
    <button type="button" class="btn btn-primary" (click)="openHistoryModal()">
      Order history
    </button>
  </div>
  <!-- Order Modal -->
  <div class="modal"  [ngClass]="{'show': isModalOpen}" tabindex="-1" role="dialog" aria-labelledby="orderModalLabel" aria-hidden="true" [ngStyle]="{'display': isModalOpen ? 'block' : 'none'}">
    <div class="modal-dialog" role="document">
      <div class="modal-content" style="background-color:snow; border-radius: 10px; box-shadow: 5px 5px 15px rgba(0,0,0,0.3);">
        <div class="modal-header">
          <h5 class="modal-title" id="orderModalLabel">Place Order</h5>
          <!--<button type="button" class="close" data-dismiss="modal" aria-label="Close">
            <span aria-hidden="true">&times;</span>
          </button>-->
        </div>
        <div class="modal-body">
          <form [formGroup]="form">
            <div class="form-group">
              <label for="stockSymbol">Stock Symbol</label>
              <input type="text" class="form-control" id="stockSymbol" formControlName="stockSymbol" placeholder="Enter Stock Symbol">
            </div>
            <div class="form-group">
              <label for="orderType">Order Type</label>
              <select class="form-control" formControlName="orderType" id="orderType">
                <option>Buy</option>
                <option>Sell</option>
              </select>
            </div>
            <div class="form-group">
              <label for="quantity">Quantity</label>
              <input type="number" class="form-control" id="quantity" formControlName="quantity" placeholder="Enter Quantity">
            </div>
          </form>
        </div>
        <div class="modal-footer">
          <button type="button" class="btn btn-secondary" data-dismiss="modal" (click)="closeModal()">Close</button>
          <button type="button" class="btn btn-primary" (click)="placeOrder()">Place Order</button>
        </div>
      </div>
    </div>
  </div>

  <!-- Order History Modal -->
  <div class="modal"  [ngClass]="{'show': isModalHistoryOpen}" tabindex="-1" role="dialog" aria-labelledby="orderModalLabel" aria-hidden="true" [ngStyle]="{'display': isModalHistoryOpen ? 'block' : 'none'}">
    <div class="modal-dialog" role="document">
      <div class="modal-content" style="background-color:snow; border-radius: 10px; box-shadow: 5px 5px 15px rgba(0,0,0,0.3);">
        <div class="modal-header">
          <h5 class="modal-title" id="orderHistoryModalLabel">Place Order</h5>
          <!--<button type="button" class="close" data-dismiss="modal" aria-label="Close">
            <span aria-hidden="true">&times;</span>
          </button>-->
        </div>
        <div class="modal-body">
          <table class="table table-striped ">
            <thead>
              <tr>
                <th scope="col">Stock Symbol</th>
                <th scope="col">Order Type</th>
                <th scope="col">Quantity</th>
                <th scope="col">Time Stamp</th>
              </tr>
            </thead>
            <tbody>
              <tr *ngFor="let order of orders">
                <td>{{order.symbol}}</td>
                <td>{{order.type}}</td>
                <td>
                  {{order.quantity}}
                </td>
                
                <td >{{order.timeStamp}}</td>
              </tr>
            </tbody>
          </table>
        </div>
        <div class="modal-footer">
          <button type="button" class="btn btn-secondary" data-dismiss="modal" (click)="closeHistoryModal()">Close</button>
          
        </div>
      </div>
    </div>
  </div>
  <div class=" table-responsive">
  <table class="table table-striped">
    <thead>
      <tr>
        <th scope="col">Title</th>
        <th scope="col">Symbol</th>
        <th scope="col">Current Price</th>
        <th scope="col">Past Price</th>
        <th scope="col">Time Stamp</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let stock of stocks">
        <td>{{stock.title}}</td>
        <td>{{stock.symbol}}</td>
        <td [ngClass]="{'price-up': stock.currentPrice > stock.priceHistories[stock.priceHistories.length - 1].price, 'price-down': stock.currentPrice < stock.priceHistories[stock.priceHistories.length - 1].price}">
          {{stock.currentPrice}}
        </td>
        <td *ngIf="stock.priceHistories && stock.priceHistories.length > 0">{{stock.priceHistories[stock.priceHistories.length - 1].price}}</td>
        <td *ngIf="stock.priceHistories && stock.priceHistories.length > 0">{{stock.priceHistories[stock.priceHistories.length - 1].timeStamp}}</td>
      </tr>
    </tbody>
  </table>
    </div>
</div>
